import React, {useState} from 'react'

const ArtItems = [
  { title: "building", description: "description", image: 'img/art/building.jpg' },
  { title: "coffee", description: "description", image: 'img/art/coffee.jpg' },
  { title: "fallentree", description: "description", image: 'img/art/fallentree.jpg' }
]

const ArtCard = ({title, description, image}) => {
  const [showModal, setShowModal] = useState(false)

  const openModal = () => {
    setShowModal(prev=> !prev)
  }

  return (
    <>
      <div className='flex flex-col md:mb-8 sm:mb-5'>
        <div className='min-h-fit w-full bg-white border-2 rounded-xl shadow-md  p-5'>
          <a href="" onClick={openModal}>
            <img src={image} className='pb-5' alt={title}/>
          </a>
          {description}
        </div>
      </div>
    </>
  )
}

export default ArtCard
export { ArtItems }
